INSERT INTO `dtb_product` (`id`, `creator_id`, `product_status_id`, `name`, `note`, `description_list`, `description_detail`, `search_word`, `free_area`, `create_date`, `update_date`, `discriminator_type`) VALUES
(4, 1, 1, 'ステレンラスト・オールドブッシュヴァイン・サンソー', NULL, NULL, 'ストロベリー、レッドチェリー、赤系果実、熟した果実、森の土、優しいオークの香り。柔らかな酸とボディ、渋みは優しく細やか。フルーティでシルキー、ピノノワールのような透明感ある綺麗なワイン。', 'サンソー', '<div class=\"productsheet__table\">   <h2><small>Stellenrust Old bushvine Cinsault</small></h2>\r\n    <table>\r\n        <tr>\r\n            <th>ワイナリー：</th>\r\n            <td>{# 関連カテゴリ #}\r\n{% if Product.ProductCategories is not empty %}\r\n    {% for ProductCategory in Product.ProductCategories %}\r\n    {% set Cat = ProductCategory.Category %}\r\n        {% if Cat.Parent.id == 62 %}\r\n            {% for Category in ProductCategory.Category.path %}\r\n                {% if Category.id != 62 %}\r\n                    <a href=\"{{ url(\'product_list\') }}?category_id={{ Category.id }}\">{{ Category.name }}</a>\r\n                {% endif %}\r\n            {% endfor %}\r\n        {% endif %}\r\n    {% endfor %}\r\n{% endif %}\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <th>原産国：</th>\r\n            <td>南アフリカ／ステレンボッシュ</td>\r\n        </tr>\r\n        <tr>\r\n            <th>タイプ：</th>\r\n            <td>\r\n{% if Product.ProductCategories is not empty %}\r\n    {% for ProductCategory in Product.ProductCategories %}\r\n    {% set Cat = ProductCategory.Category %}\r\n        {% if Cat.Parent.id == 27 or Cat.Parent.id == 28 or Cat.Parent.id == 29 or Cat.Parent.id == 39 %}\r\n            {% for Category in ProductCategory.Category.path %}\r\n                <a href=\"{{ url(\'product_list\') }}?category_id={{ Category.id }}\">{{ Category.name }}</a>\r\n                {%- if loop.last == false %}<span>＞</span>{% endif %}\r\n            {% endfor %}\r\n        {% endif %}\r\n    {% endfor %}\r\n{% endif %}\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <th>主要品種：</th>\r\n            <td>{{ Product.search_word }}</td>\r\n        </tr>\r\n        \r\n        <tr>\r\n            <th>アルコール度数：</th>\r\n            <td>13.0％未満</td>\r\n        </tr>\r\n        <tr>\r\n            <th>容量：</th>\r\n            <td>750ml</td>\r\n        </tr>\r\n        <tr>\r\n            <th>ヴィンテージ：</th>\r\n            <td>2020</td>\r\n        </tr>\r\n        <tr>\r\n            <th>受賞／評価：</th>\r\n            <td>-\r\n            </td>\r\n        </tr>\r\n\r\n    </table>\r\n</div>\r\n<div class=\"ec-productRole__description\">\r\nボタラリー・ヒルズの風化した花崗岩質の畑で1964年に植樹。2日間コールドマセラシオン後、ブドウの皮と一緒にローヌとボルドーの酵母を使ってコンクリートのオープンタンクで発酵。ブドウの皮と一緒にステンレスタンクに移し7-10日漬け込み、マロラクティック発酵を経て、フレンチオークに移して16ヶ月熟成（新樽10％、残りは2-3年目の樽）。<br>ストロベリー、レッドチェリー、赤系果実、熟した果実、森の土、優しいオークの香り。柔らかな酸とボディ、渋みは優しく細やか。フルーティでシルキー、ピノノワールのような透明感ある綺麗なワイン。古木のブドウ由来の優しい口あたりでエレガントなスタイル。<br>日々飲みたい軽快な心地の良いワイン。15度前後くらいの気持ち冷やして提供するのが良いと思います。<br>焼鳥、和食、焼きそば、中華料理、アジア系料理など。\r\n</div>', '2020-09-26 06:39:27', '2023-11-13 11:31:10', 'product'),
(5, 1, 1, 'ステレンラスト・シュナン・ド・ミュスカ・ノーブルレイト・ハーベスト', NULL, NULL, 'ボタラリー・ヒルズのシュナンブランのブッシュヴァインの畑は樹齢51年以上。マスカットオブアレキサンドリアは樹齢71年以上。1928年創業時のオリジナル・ラベルを採用し、昔のことを忘れないようにしている。また自社の一番古い畑のブドウを使用している。', 'シュナンブラン,マスカット・オブ・アレキサンドリア', '<div class=\"productsheet__table\">   <h2><small>Stellenrust Chenin D\' Muscat Noble Late Harvest</small></h2>\r\n    <table>\r\n        <tr>\r\n            <th>ワイナリー：</th>\r\n            <td>{# 関連カテゴリ #}\r\n{% if Product.ProductCategories is not empty %}\r\n    {% for ProductCategory in Product.ProductCategories %}\r\n    {% set Cat = ProductCategory.Category %}\r\n        {% if Cat.Parent.id == 62 %}\r\n            {% for Category in ProductCategory.Category.path %}\r\n                {% if Category.id != 62 %}\r\n                    <a href=\"{{ url(\'product_list\') }}?category_id={{ Category.id }}\">{{ Category.name }}</a>\r\n                {% endif %}\r\n            {% endfor %}\r\n        {% endif %}\r\n    {% endfor %}\r\n{% endif %}\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <th>原産国：</th>\r\n            <td>南アフリカ／ステレンボッシュ</td>\r\n        </tr>\r\n        <tr>\r\n            <th>タイプ：</th>\r\n            <td>\r\n{% if Product.ProductCategories is not empty %}\r\n    {% for ProductCategory in Product.ProductCategories %}\r\n    {% set Cat = ProductCategory.Category %}\r\n        {% if Cat.Parent.id == 27 or Cat.Parent.id == 28 or Cat.Parent.id == 29 or Cat.Parent.id == 39 %}\r\n            {% for Category in ProductCategory.Category.path %}\r\n                <a href=\"{{ url(\'product_list\') }}?category_id={{ Category.id }}\">{{ Category.name }}</a>\r\n                {%- if loop.last == false %}<span>＞</span>{% endif %}\r\n            {% endfor %}\r\n        {% endif %}\r\n    {% endfor %}\r\n{% endif %}\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <th>主要品種：</th>\r\n            <td>{{ Product.search_word }}</td>\r\n        </tr>\r\n        \r\n        <tr>\r\n            <th>アルコール度数：</th>\r\n            <td>10.9％未満</td>\r\n        </tr>\r\n        <tr>\r\n            <th>容量：</th>\r\n            <td>375ml</td>\r\n        </tr>\r\n        <tr>\r\n            <th>ヴィンテージ：</th>\r\n            <td>2021</td>\r\n        </tr>\r\n        <tr>\r\n            <th>受賞／評価：</th>\r\n            <td>[Platter SA 2019’] 2016VT：4.5 STAR<br>\r\n[Tim Atkin Report 2018’] 2016VT：94 Point\r\n            </td>\r\n        </tr>\r\n\r\n    </table>\r\n</div>\r\n<div class=\"ec-productRole__description\">\r\nボタラリー・ヒルズのシュナンブランのブッシュヴァインの畑は樹齢51年以上。マスカットオブアレキサンドリアは樹齢71年以上。シュナンブランは4月からの霧（きり）により貴腐菌が付着する。マスカットオブアレキサンドリアは、ブドウが完熟後、人工的にブドウの房の上の茎をペンチで曲げて、ブドウに水分を行きにくくすることによってドライレーズン状にしていく。<br>5月下旬の寒い夜に収穫。破砕後、一部スキンコンタクト、果汁は古いオーク樽に入り2-3週間かけて、それぞれの品種ごとに分けて天然発酵。12か月熟成しブレンド。<br>香り、風味ともにアプリコットを強く感じる。他にもハチミツ、熟したトロピカルフルーツ、キャンディなどの香り。豊かな酸、凝縮した果実味、リッチでなめらか、濃厚な味。甘いハチミツのような味わいですが、しっかりした酸味もあり、後口に甘味が残らずバランス良く出来ている。最後にほんのりスパイスの感じもある。<br>南アフリカを代表するシュナンブラン生産者のステレンラストだからこそ作れる樹齢の古いブドウの濃縮感たっぷりのスペシャルなデザートワイン。<br>チーズ、ベイクドチーズケーキ、焼きプリン、焼菓子など。\r\n</div>', '2020-09-26 06:43:26', '2023-11-13 11:37:19', 'product'),
